    public void Init(Pico8Functions pico8)
    {
        p8 = pico8;
        int[] mapInt = Pico8Utils.DataToArray(Pico8Utils.MapFlip(MapData), 2);
        string s = "";
        for (int i = 0; i < mapInt.Length; i++)
        {
            char c1 = (char)(mapInt[i] / 91 + 35);
            char c2 = (char)(mapInt[i] % 91 + 35);
            s = s + $"{c1}{c2}";
            if (i % MapDimensions.x == MapDimensions.x - 1)
            {
                s = s + "\n";
            }
        }
        Console.WriteLine(s);
    }

